def allowed_file(filename):
    return '.' in filename and filename.rsplit('.', 1)[1].lower() in ALLOWED_EXTENSIONS

@app.route('/', methods=['GET', 'POST'])
def upload_file():
    if request.method == 'POST':
        if 'file' not in request.files:
            return redirect(request.url)
        
        file = request.files['file']
        if file.filename == '':
            return redirect(request.url)
        
        if file and allowed_file(file.filename):
            filename = secure_filename(file.filename)
            file.save(os.path.join(app.config['UPLOAD_FOLDER'], filename))
        
        return '', 200

    files = os.listdir(app.config['UPLOAD_FOLDER'])
    return render_template_string(HTML, files=files, url=app.config['SERVER_URL'])

@app.route('/static/<path:filename>')
def static_files(filename):
    return send_from_directory(STATIC_FOLDER, filename)
  
@app.route('/uploads/<filename>')
def uploaded_file(filename):
    return send_from_directory(app.config['UPLOAD_FOLDER'], filename)

def get_ip():
    s = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
    try:
        s.connect(("10.255.255.255", 1))
        IP = s.getsockname()[0]
    except Exception:
        IP = "127.0.0.1"
    finally:
        s.close()
    return IP

def gerar_qr_code(url):
    qr = qrcode.make(url)
    qr.save(QR_IMAGE_PATH)

def run_flask():
    app.run(host="0.0.0.0", port=80, threaded=True)

def main():
    ip = get_ip()
    porta = 80
    url = f"http://{ip}:{porta}"
    app.config['SERVER_URL'] = url
    gerar_qr_code(url)

    # Inicia o Flask em background
    threading.Thread(target=run_flask, daemon=True).start()

    # Abre o app com interface WebView
    webview.create_window("Servidor de Upload", url, width=1024, height=768)
    webview.start()

if __name__ == '__main__':
    main()
